mapper.select:
select

from blacklist
where account = #{account,jdbcType=VARCHAR}
and user_id = #{userId,jdbcType=BIGINT}

select

from blacklist
where user_id = #{userId,jdbcType=BIGINT}

  and good_type_id = #{goodTypeId,jdbcType=BIGINT}


  and account like concat('%',#{account,jdbcType=BIGINT},'%')

order by id desc
