mapper.select:
 select

from finance_record fc
left join user u on u.id = fc.user_id
where 1=1

  and fc.user_id = #{userId}


  and fc.transaction_type = #{transactionType,jdbcType=INTEGER}


  AND fc.create_time between #{startTime} and #{endTime}

mapper.insert:

  SELECT LAST_INSERT_ID()

insert into finance_record (user_id, transaction_before, transaction_after, 
  transaction_money, transaction_type, remark, 
  status, create_emp, create_time
  )
values (#{userId,jdbcType=BIGINT}, #{transactionBefore,jdbcType=DECIMAL}, #{transactionAfter,jdbcType=DECIMAL}, 
  #{transactionMoney,jdbcType=DECIMAL}, #{transactionType,jdbcType=INTEGER}, #{remark,jdbcType=VARCHAR}, 
  #{status,jdbcType=INTEGER}, #{createEmp,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}
  )
