mapper.select:
SELECT

FROM log
WHERE company_id = #{companyId}

  and user_name like concat('%',#{userName,jdbcType=VARCHAR},'%')





  and create_time between #{startTime} and #{endTime}

ORDER BY create_time desc

mapper.insert:
insert into log (id, user_name, user_id,
  company_id, action, operation, 
  result, ip, create_time
  )
values (#{id,jdbcType=BIGINT}, #{userName,jdbcType=VARCHAR}, #{userId,jdbcType=BIGINT}, 
  #{companyId,jdbcType=BIGINT}, #{action,jdbcType=VARCHAR}, #{operation,jdbcType=VARCHAR}, 
  #{result,jdbcType=VARCHAR}, #{ip,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}
  )
